import './App.css';
import React, {useState} from 'react'
import Context from './context'
import BaseNav from './Basic/header'
import BaseFooter from './Basic/footer'
import UserList from './Users/UserList'

import Nav from 'react-bootstrap/Nav'
import Table from 'react-bootstrap/Table'

document.body.style = 'background: rgba(0, 0, 0, 0.3);';


function App() {

  const [users, setUsers ]= useState(
  [
    {id: '1', name: 'John', lastname: 'Doe', age: 24, gender: 'Male'},
    {id: '2', name: 'Albert', lastname: 'Einstein', age: 124, gender: 'Male'},
    {id: '3', name: 'Feen', lastname: 'Fargoos', age: 45, gender: 'Male'},
    {id: '4', name: 'Lili', lastname: 'Batonchek', age: 24, gender: 'Female'},
    {id: '5', name: 'Karen', lastname: 'Lawson', age:67, gender: 'Female'},
    {id: '6', name: 'Marshal', lastname: 'Batonchek', age: 24, gender: 'Male'},
    {id: '7', name: 'Ted', lastname: 'Mosbey', age:67, gender: 'Male'}
  ])
  const [admins, setAdmins] = useState(
    [
        {id: 'A1', name: 'Bob', lastname: 'Doe', age: 24, gender: 'Male'},
        {id: 'A2', name: 'Bob', lastname: 'Einstein', age: 124, gender: 'Male'},
        {id: 'A3', name: 'Bob', lastname: 'Fargoos', age: 45, gender: 'Male'},
        {id: 'A4', name: 'Bob', lastname: 'Batonchek', age: 24, gender: 'Female'},
        {id: 'A5', name: 'Bob', lastname: 'Lawson', age:67, gender: 'Female'},
        {id: 'A6', name: 'Bob', lastname: 'Batonchek', age: 24, gender: 'Male'},
        {id: 'A7', name: 'Bob', lastname: 'Mosbey', age:67, gender: 'Male'}
  ])
  const [superUsers, setSuperUsers] = useState(
    [
        {id: 'S1', name: 'Steven', lastname: 'Doe', age: 24, gender: 'Male'},
        {id: 'S2', name: 'Steven', lastname: 'Einstein', age: 124, gender: 'Male'},
        {id: 'S3', name: 'Steven', lastname: 'Fargoos', age: 45, gender: 'Male'},
        {id: 'S4', name: 'Steven', lastname: 'Batonchek', age: 24, gender: 'Female'},
        {id: 'S5', name: 'Steven', lastname: 'Lawson', age:67, gender: 'Female'},
        {id: 'S6', name: 'Steven', lastname: 'Batonchek', age: 24, gender: 'Male'},
        {id: 'S7', name: 'Steven', lastname: 'Mosbey', age:67, gender: 'Male'}
  ])
  const [location, setLocation] = useState("Users")
  const [data, setData] = useState(users)

  function removeUser(id){
    if (location === "Users"){
      setUsers(users.filter(user => user.id !== id))
      console.log(users)
    }else if(location === "Admins"){
      setAdmins()
    }
  }
  function editUser(newUser){
      setUsers(
        users.map(user =>{
          if (user.id === newUser.id){
            user.name = newUser.name
            user.lastname = newUser.lastname
            user.age = newUser.age
            user.gender = newUser.gender
          }
          return user
        })
      ) 
      }
  function createUser(newUser){
    setUsers(prevUsers => [...prevUsers, newUser])
  }
  function setInfo(info){
    setLocation(info)
    if(info==="Users"){
      setData(users)
    }else if(info==="Admins"){
      setData(admins)
    }else{
      setData(superUsers)
    }
  }
      return (


    <Context.Provider value = {{ removeUser, editUser, createUser}}>
    <div className="App">


          <BaseNav/>



          <div className="super-content">
            <div className="tabs">
            <Nav variant="tabs" defaultActiveKey="link-1">
              <Nav.Item >
                <Nav.Link eventKey="link-1" className="custom-item" onClick={setInfo.bind(null, "Users")}>Users</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="link-2" className="custom-item" onClick={setInfo.bind(null, "Admins")}>Admins</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="link-3" className="custom-item" onClick={setInfo.bind(null, "SuperUsers")}>
                  SuperUsers
                </Nav.Link>
                {console.log(location)}
              </Nav.Item>
            </Nav>
            </div>
          <div className="content-main">

            <div className="list-inside">
            <Table>

        <thead>
            <tr>
                <th>Name</th>
                <th>Lastname</th>
                <th>Age</th>
                <th>Gender</th>
                <th>Edit</th>
            </tr>
        </thead>
          <UserList data={data}/>

    </Table>

            </div>
          </div>
        </div>
          <BaseFooter />

    </div>
    </Context.Provider>
  );
}

export default App;
