import React, {useContext} from 'react'
import PropTypes from 'prop-types'
import Context from '../context'
import Edit from '../Editing/Edit'




function UserItem({user, key}){
    const { removeUser }= useContext(Context)
    const styles ={
        button:{
            marginRight: '1rem',
            minWidth: '5.5rem',
            marginBottom: '2px',
        }, 
    };


    return(        
        <tr>
            <td>{user.name}</td>
            <td>{user.lastName}</td>
            <td>{user.age}</td>
            <td>{user.sex}</td>
            <td className='editing'>
                <form style={styles.form}>
                    <button type="button" style={styles.button} className='btn btn-success my-button' onClick={removeUser.bind(null, user.id)}>Remove</button>
                    <Edit />
                </form>
            </td>
        </tr>

    )

}

UserItem.propTypes = {
    props: PropTypes.object
}

export default UserItem
