import React from 'react'
import Context from '../context'

import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import Col from 'react-bootstrap/Col'


 class Edit extends React.Component{
    constructor(props){
        super(props)

        this.state ={
            show: false,
            edditedUser: {
                id: this.props.user.id,
                name: this.props.user.name,
                lastname: this.props.user.lastname,
                age: this.props.user.age,
                gender: this.props.user.gender,
        }

        };
    }

    render(){
        const {removeUser, editUser} = this.context
        const handleClose = () => this.setState({show: false});
        const handleShow = () => this.setState({show:true});
        const saveAndClose = (userObj) => {
            editUser(userObj)
            handleClose()
        }

        const setName = (newName) => this.Setstate(prev =>{
            return{
                ...prev,
                name:newName
            }
        })
        const setLastname = (newLastname) => this.Setstate(prev =>{
            return{
                ...prev,
                lastname:newLastname
            }
        })
        const setAge = (newAge) => this.Setstate(prev =>{
            return{
                ...prev,
                age:newAge
            }
        })
        const setGender = (newGender) => this.Setstate(prev =>{
            return{
                ...prev,
                gender:newGender
            }
        })

        return(

            <React.Fragment>   
                <>
                    <Button variant="btn btn-success my-button" onClick={handleShow}>
                        Edit
                    </Button>

                    <Modal show={this.state.show} 
                           onHide={handleClose} 
                           animation={true}
                           size="lg" 
                           aria-labelledby="contained-modal-title-vcenter" 
                           centered className="modal-edited">

                        <Modal.Body>
                            <Form>
                            <Form.Row>    
                                    <Form.Group as={Col} controlId="formBasicName">
                                        <Form.Label>Name</Form.Label>
                                        <Form.Control type="text" defaultValue={this.state.edditedUser.name} onChange={event => setName(event.target.value)}/>
                                        <Form.Text className="text-muted">

                                        </Form.Text>
                                    </Form.Group>

                                    <Form.Group as={Col} controlId="formBasicName">
                                        <Form.Label>Lastname</Form.Label>
                                        <Form.Control type="text" defaultValue={this.state.edditedUser.lastname} onChange={event => setLastname(event.target.value)}/>
                                        <Form.Text className="text-muted">

                                        </Form.Text>
                                    </Form.Group>
                                </Form.Row>

                                <Form.Row>
                                    <Form.Group as={Col} controlId="formBasicName">
                                        <Form.Label>Age</Form.Label>
                                        <Form.Control type="text" defaultValue={this.state.edditedUser.age} onChange={event => setAge(event.target.value)}/>
                                        <Form.Text className="text-muted">

                                        </Form.Text>
                                    </Form.Group>
                                    <Form.Group as={Col} controlId="formBasicName">
                                        <Form.Label>Gender</Form.Label>
                                        <Form.Control as="select" defaultValue={this.state.edditedUser.gender} onChange={event => setGender(event.target.value)}>
                                            <option>Male</option>
                                            <option>Female</option>
                                            <option>Something Else</option>
                                        </Form.Control>
                                    </Form.Group>
                                </Form.Row>
                                </Form>
                        </Modal.Body>
                        <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={saveAndClose.bind(null, this.state.edditedUser)}>
                            Save Changes
                        </Button>
                        </Modal.Footer>
                    </Modal>
                    </>

            </React.Fragment>
)
        }}


Edit.contextType = Context
export default Edit
