import React, {useState} from 'react'
import PropTypes from 'prop-types'
import render from 'react-dom'

import MyModal from '../Editing/Modal'



function UserItem(props, ModalShow){
    console.log
    const styles ={
        button:{
            marginRight: '1rem',
            minWidth: '5.5rem',
            marginBottom: '2px',
        }, 
    };
    const [modalShow, setModalShow] = React.useState(false);
    return(        

        <tr>
            <td>{props.user.name}</td>
            <td>{props.user.lastName}</td>
            <td>{props.user.age}</td>
            <td>{props.user.sex}</td>
            <td className='editing'>
                <form style={styles.form}>
                    <button style={styles.button} className='btn btn-success' onCLick={() => ModalShow(true)}>Remove</button>
                    <button style={styles.button} className='btn btn-danger'>Edit</button>
                </form>
            </td>
        </tr>

    )

}

UserItem.propTypes = {
    props: PropTypes.object.isRequired
}

export default UserItem
