import React, {useContext} from 'react'
import PropTypes from 'prop-types'
import Context from '../context'




function UserItem(user){
    const { removeUser }= useContext(Context)
    const styles ={
        button:{
            marginRight: '1rem',
            minWidth: '5.5rem',
            marginBottom: '2px',
        }, 
    };
    function Preventing(event){
        event.prevenDefault()
    }

    return(        
        <tr>
            <td>{user.name}</td>
            <td>{user.lastName}</td>
            <td>{user.age}</td>
            <td>{user.sex}</td>
            <td className='editing'>
                <form style={styles.form}>
                    <button type="button" style={styles.button} className='btn btn-success' onClick={removeUser.bind(null, props.user.id)}>Remove</button>
                    <button type="button" style={styles.button} className='btn btn-danger'>Edit</button>
                </form>
            </td>
        </tr>

    )

}

UserItem.propTypes = {
    props: PropTypes.object
}

export default UserItem
