import React from 'react'
import '../Modal.css'

export default class Edit extends React.Component{
    state = {
        isOpen: false,        
    }

    render(){
        return(
            <React.Fragment>

                <button type="button" className='btn btn-danger my-button' onClick={() => setState({ isOpen:true })}>
                    Edit
                </button>
                {this.state.isOpen && (
                    <div className="modal">
                        <div className="modal-body ">
                            <h1>Header</h1>
                            <p>Some info</p>
                            <button type="button" className='btn btn-danger my-button' onClick={() => this.state({isOpen:false})}>Close</button>
                        </div>
                    </div>
                )}
            </React.Fragment>
        )
    }
}
