import React from 'react'
import '../Modal.css'

export default class Edit extends React.Component{
    state = {
        isOpen: false,
        styles: {
            button:{
                marginRight: '1rem',
                minWidth: '5.5rem',
                marginBottom: '2px',
            }, 
        }
    }

    render(){
        return(
            <React.Fragment>

                <button type="button" style= {this.state(styles.button)} className='btn btn-danger' onClick={() => this.state({isOpen:true})}>
                    Edit
                </button>
                <div className="modal">
                    <div className="modal-body ">
                        <h1>Header</h1>
                        <p>Some info</p>
                        <button type="button" style={this.state.styles.button} className='btn btn-danger' onClick={() => this.state({isOpen:false})}>Close</button>
                    </div>
                </div>
            </React.Fragment>
        )
    }
}
